import { render, screen } from '@testing-library/react';
import { Provider } from 'react-redux';
import { BrowserRouter as Router } from 'react-router-dom';
import { createStore } from 'redux';
import App from './App';
import rootReducer from './reducers';

const store = createStore(rootReducer);

describe('App', () => {
  beforeEach(() => {
    render(
      <Provider store={store}>
        <Router>
          <App />
        </Router>
      </Provider>
    );
  });

  test('renders Header component when authenticated', () => {
    store.dispatch({ type: 'USER_LOADED', payload: { isAuthenticated: true } });
    expect(screen.getByText(/Header Component Text/i)).toBeInTheDocument();
  });

  test('does not render Header component when not authenticated', () => {
    store.dispatch({ type: 'USER_LOADED', payload: { isAuthenticated: false } });
    expect(screen.queryByText(/Header Component Text/i)).toBeNull();
  });

  test('renders Home component when authenticated and on / route', () => {
    store.dispatch({ type: 'USER_LOADED', payload: { isAuthenticated: true } });
    expect(screen.getByText(/Home Component Text/i)).toBeInTheDocument();
  });

  test('renders Login component when not authenticated and on / route', () => {
    store.dispatch({ type: 'USER_LOADED', payload: { isAuthenticated: false } });
    expect(screen.getByText(/Login Component Text/i)).toBeInTheDocument();
  });

  // Add similar tests for other routes
});
